package org.compiere.model;
import java.sql.PreparedStatement;import java.sql.ResultSet;import java.util.ArrayList;import java.util.List;import java.util.Properties;import org.compiere.util.DB;/** * @author Omar Torres * */public class MEXMEEstatusClas extends X_EXME_Estatus_Clas {   /**	 * 	 */	private static final long serialVersionUID = 1215157692882459333L;    /**     * @param ctx     * @param EXME_Cama_ID     * @param trxName     */    public MEXMEEstatusClas(Properties ctx, int EXME_Estatus_Clas_ID, String trxName) {		super(ctx, EXME_Estatus_Clas_ID, trxName);	}    /**     * @param ctx     * @param rs     * @param trxName     */    public MEXMEEstatusClas(Properties ctx, ResultSet rs, String trxName) {		super(ctx, rs, trxName);	}         /** * Regresa ID del estatus de la clasificacion * @param Value  * @return */	public static int getEXME_Estatus_Clas_ID(Properties ctx, String value) {		String sql = "SELECT EXME_Estatus_Clas_ID FROM  EXME_Estatus_Clas WHERE Estatus= ? ";		sql = MEXMELookupInfo.addAccessLevelSQL(ctx, sql, "EXME_Estatus_Clas");		return DB.getSQLValue(null, sql.toString(), value);	}            public static MEXMEEstatusClas[] getEstatusClas(Properties ctx, String trxName) {        String sql = null;        PreparedStatement pstmt = null;        ResultSet rs = null;        List<MEXMEEstatusClas> resultados = new ArrayList<MEXMEEstatusClas>();        try{            sql = "SELECT * FROM EXME_Estatus_Clas WHERE IsActive = 'Y'";                        sql = MEXMELookupInfo.addAccessLevelSQL(ctx, sql, "EXME_Estatus_Clas");                        pstmt = DB.prepareStatement(sql, trxName);                        rs = pstmt.executeQuery();            while (rs.next()) {                MEXMEEstatusClas estatusClas = new MEXMEEstatusClas(ctx, rs, trxName);                 resultados.add(estatusClas);            }        }        catch(Exception e){                   }        finally{        	DB.close(rs, pstmt);        }        MEXMEEstatusClas[] reenviar = new MEXMEEstatusClas[resultados.size()];        resultados.toArray(reenviar);        return reenviar;    }
}