package org.compiere.model;
import java.sql.PreparedStatement;import java.sql.ResultSet;import java.util.Properties;import java.util.logging.Level;import org.compiere.util.CLogger;import org.compiere.util.DB;

/** * Modelo para Vigencia de la Clasificacion * * <b>Fecha:</b> 08/Febrero/2005<p> * <b>Modificado: </b> $Author: gisela $<p> * <b>En :</b> $Date: 2006/04/18 14:38:02 $<p> * * @author Gisela Lee * @version $Revision: 1.1 $ */public class MEXMEVigencia extends X_EXME_Vigencia {    /**	 * 	 */	private static final long serialVersionUID = 1L;	/** Static Logger               */    private static CLogger      log = CLogger.getCLogger (MEXMEVigencia.class);        /**     * @param ctx     * @param ID     */    public MEXMEVigencia(Properties ctx, int ID, String trx) {        super(ctx, ID, trx);    }    /**     * @param ctx     * @param rs     */    public MEXMEVigencia(Properties ctx, ResultSet rs, String trx) {        super(ctx, rs, trx);    }        /**     * Obtenemos la vigencia del estudio por empresa     * @param ctx El contexto de la aplicacion     * @return La vigencia del estudio socio economico      */    public static MEXMEVigencia getByADClient(Properties ctx, String trxName){        MEXMEVigencia vig = null;                String sql = "SELECT * FROM EXME_Vigencia WHERE IsActive = 'Y'";                sql = MEXMELookupInfo.addAccessLevelSQL(ctx, sql.toString(), "EXME_Vigencia");		PreparedStatement pstmt = null;		ResultSet rs = null;		try {			pstmt = DB.prepareStatement(sql, trxName);			rs = pstmt.executeQuery();			if (rs.next()) {				vig = new MEXMEVigencia(ctx, rs, trxName);			}        }catch (Exception e) {        	log.log(Level.SEVERE, "get", e);        } finally {       		DB.close(rs, pstmt);       		pstmt=null;       		rs=null;       }
       return vig;    }}